mapper.select:
select
count(goods_code)
from t_cart
where goods_code = #{goodsCode}
and user_code = #{userId}
and is_deleted = 0

select
order_sum orderSum
from t_cart
where goods_code = #{goodsCode}
and user_code = #{userId}
and is_deleted = 0

mapper.update:
update t_cart
set
    order_sum = #{orderSum} + #{NewOrderSum},
    update_time = now(),
    update_name = #{userId}
where goods_code = #{goodsCode}
and user_code = #{userId}
