mapper.select:
select sum(order_sum) orderSum, goods_code goodsCode from t_order_detail where is_deleted = 0 and order_code in
#{orderCode}
group by goods_code

update.foreach:
update t_goods
set
stock = stock + #{item.orderSum}
where goods_code = #{item.goodsCode}

mapper.update:
update t_order set order_state = #{orderState}, update_time = now(), update_name = #{userId}, version = version + 1 where order_code in
#{orderCode}
and version in
#{version}
