mapper.select:
select count(store_code)
from t_store
where is_deleted = 0
and (license_code = #{licenseCode}
or user_code = #{userCode})

    and store_code != #{storeCode}

select a.store_code storeCode, a.store_name storeName, a.phone phone, a.address address, b.user_name userName, b.user_acct userAcct, a.village_code villageCode, a.version version from t_store a left join t_sys_user b on a.user_code = b.user_code where a.is_deleted = 0
and a.user_code = #{userCode}
and a.store_code like concat('%', #{storeCode}, '%')
and a.store_name like concat('%', #{storeName}, '%')
and b.user_name like concat('%', #{userName}, '%')
and a.province_code = #{provinceCode}
and a.city_code = #{cityCode}
and a.area_code = #{areaCode}
order by a.create_time desc

select distinct store_code from t_order where store_code in
#{item}
and order_state != 5
